function createNavbar(){const e=window.location.pathname,t=`\n        <div class="py-0">\n            <div class="container mx-auto text-center text-white">\n                <div class="flex items-center justify-between">\n                    <a href="/dashboard" class="flex-none">\n                        <img src="/logo.png" alt="NanoTrack Logo" class="max-h-8 ml-4">\n                    </a>\n                    <nav class="flex-1 text-center">\n                        <a href="/dashboard" class="mx-2 p-2 ${"/dashboard"===e?"border-b-2 border-white":"hover:border-b-2 hover:border-white"}">Dashboard</a>\n                        <a href="/campaigns" class="mx-2 p-2 ${e.includes("/campaigns")?"border-b-2 border-white":"hover:border-b-2 hover:border-white"}">Campaigns</a>\n                        \x3c!--<a href="/reports" class="mx-2 p-2 ${"/reports"===e?"border-b-2 border-white":"hover:border-b-2 hover:border-white"}">Reports</a>\n                        <a href="/export" class="mx-2 p-2 ${"/export"===e?"border-b-2 border-white":"hover:border-b-2 hover:border-white"}">Export</a>\n                        <a href="/settings" class="mx-2 p-2 ${"/settings"===e?"border-b-2 border-white":"hover:border-b-2 hover:border-white"}">Settings</a>--\x3e\n                    </nav>\n                    \x3c!-- Spacer element --\x3e\n                    <div class="flex-none" style="width: 160px;"></div>\n                    <form action="/logout" method="post" class="flex-none mr-4">\n                        <button type="submit" class="bg-transparent hover:bg-gray-700 text-white font-semibold hover:text-white py-2 px-4 border border-white rounded-lg">Logout</button>\n                    </form>\n                </div>\n            </div>\n        </div>\n    `;document.getElementById("header").innerHTML=t}function createFooter(){document.getElementById("footer").innerHTML='\n        <div class="custom-bg py-0">\n            <div class="container mx-auto text-center text-white text-xs">\n                <div>\n                    <a href="https://github.com/DillonBaird/NanoTrack" target="_blank" class="text-white font-bold">NanoTrack v1.0</a> - Made with ❤️ by <a href="https://DillonBaird.io" target="_blank" class="text-white font-bold">Dillon Baird</a>\n                </div>\n            </div>\n        </div>\n    '}document.addEventListener("DOMContentLoaded",(()=>{createNavbar(),createFooter()}));